import { useContext } from 'react';
import classes from '../Cart/Cart.module.css';
import CartContext from '../store/cart-context';
import Modal from '../Ui/Modal';
import CartItem from '../Cart/CartItem';

const Cart = (props) => {
    const cartCtx = useContext(CartContext);
    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
    const hasCartItem = cartCtx.items.length > 0;

    const cartItemAddHandler = (item) => {
        cartCtx.addItem({...item,amount:1});
    }

    const cartItemRemoveHandler = (id) => {
        cartCtx.removeItem(id);
    }
    const CartItems = <ul className={classes['cart-items']}>{cartCtx.items.map((item) => {
       return <CartItem 
        key={item.id} 
        name={item.name}
        amount={item.amount} price={item.price}
        onAdd={cartItemAddHandler.bind(null,item)}
        onRemove={cartItemRemoveHandler.bind(null,item.id)}
        />
    })}</ul>


    return (
        <Modal onClose={props.closeCartModal}>
            {CartItems}
            <div className={classes.total}>
                <span >Total Amount</span>
                <span >{totalAmount}</span>
            </div>
            <div className={classes.actions}>
                <button className={classes['button--alt']} onClick={props.closeCartModal}>Close</button>
                {hasCartItem && <button className={classes.button}>Order</button>}
            </div>
        </Modal>
    );

}

export default Cart;
